import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import CompanyList from './components/CompanyList';
import StockChart from './components/StockChart';
import { stockAPI } from './services/api';
import { AlertCircle, RefreshCw } from 'lucide-react';

function App() {
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [stockData, setStockData] = useState(null);
  const [historicalData, setHistoricalData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [chartLoading, setChartLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isOnline, setIsOnline] = useState(navigator.onLine);
  const [period, setPeriod] = useState('1mo');

  // Check online status
  useEffect(() => {
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  // Load companies on mount
  useEffect(() => {
    loadCompanies();
  }, []);

  // Load stock data when company is selected
  useEffect(() => {
    if (selectedCompany) {
      loadStockData(selectedCompany.symbol);
    }
  }, [selectedCompany, period]);

  const loadCompanies = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await stockAPI.getCompanies();
      setCompanies(data);
    } catch (err) {
      setError('Failed to load companies. Please try again.');
      console.error('Error loading companies:', err);
    } finally {
      setLoading(false);
    }
  };

  const loadStockData = async (symbol) => {
    try {
      setChartLoading(true);
      setError(null);
      
      // Load current stock data and historical data in parallel
      const [currentData, historyData] = await Promise.all([
        stockAPI.getStockData(symbol),
        stockAPI.getStockHistory(symbol, period)
      ]);
      
      setStockData(currentData);
      setHistoricalData(historyData);
      
      // Update company in the list with current price
      setCompanies(prev => 
        prev.map(company => 
          company.symbol === symbol 
            ? { ...company, current_price: currentData.current_price, last_updated: currentData.last_updated }
            : company
        )
      );
    } catch (err) {
      setError(`Failed to load data for ${symbol}. Please try again.`);
      console.error('Error loading stock data:', err);
    } finally {
      setChartLoading(false);
    }
  };

  const handleCompanySelect = (company) => {
    setSelectedCompany(company);
    setStockData(null);
    setHistoricalData(null);
  };

  const handlePeriodChange = (newPeriod) => {
    setPeriod(newPeriod);
  };

  const handleRefresh = () => {
    if (selectedCompany) {
      loadStockData(selectedCompany.symbol);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header isOnline={isOnline} />
      
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        {error && (
          <div className="mb-6 bg-danger-50 border border-danger-200 rounded-lg p-4">
            <div className="flex items-center">
              <AlertCircle className="w-5 h-5 text-danger-600 mr-2" />
              <span className="text-danger-800">{error}</span>
              <button
                onClick={() => setError(null)}
                className="ml-auto text-danger-600 hover:text-danger-800"
              >
                ×
              </button>
            </div>
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* Left Panel - Company List */}
          <div className="lg:col-span-1">
            <CompanyList
              companies={companies}
              selectedCompany={selectedCompany}
              onCompanySelect={handleCompanySelect}
              loading={loading}
            />
          </div>

          {/* Main Panel - Stock Chart */}
          <div className="lg:col-span-3">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-semibold text-gray-900">
                Stock Analysis
              </h2>
              {selectedCompany && (
                <button
                  onClick={handleRefresh}
                  disabled={chartLoading}
                  className="flex items-center px-3 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  <RefreshCw className={`w-4 h-4 mr-2 ${chartLoading ? 'animate-spin' : ''}`} />
                  Refresh
                </button>
              )}
            </div>
            
            <StockChart
              historicalData={historicalData}
              stockData={stockData}
              loading={chartLoading}
              period={period}
              onPeriodChange={handlePeriodChange}
            />
          </div>
        </div>

        {/* Footer */}
        <footer className="mt-12 pt-8 border-t border-gray-200">
          <div className="text-center text-gray-500 text-sm">
            <p>
              Data provided by Yahoo Finance • Built with React & FastAPI
            </p>
            <p className="mt-1">
              Last updated: {new Date().toLocaleString()}
            </p>
          </div>
        </footer>
      </main>
    </div>
  );
}

export default App;
