import React from 'react';
import { BarChart3, Wifi, WifiOff } from 'lucide-react';

const Header = ({ isOnline }) => {
  return (
    <header className="bg-white shadow-sm border-b border-gray-200">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center">
            <BarChart3 className="w-8 h-8 text-primary-600 mr-3" />
            <div>
              <h1 className="text-xl font-bold text-gray-900">
                Stock Market Dashboard
              </h1>
              <p className="text-sm text-gray-600">
                Real-time stock data and analytics
              </p>
            </div>
          </div>
          
          <div className="flex items-center space-x-4">
            <div className="flex items-center">
              {isOnline ? (
                <Wifi className="w-4 h-4 text-success-600 mr-2" />
              ) : (
                <WifiOff className="w-4 h-4 text-danger-600 mr-2" />
              )}
              <span className={`text-sm font-medium ${
                isOnline ? 'text-success-600' : 'text-danger-600'
              }`}>
                {isOnline ? 'Online' : 'Offline'}
              </span>
            </div>
            
            <div className="text-sm text-gray-500">
              {new Date().toLocaleDateString('en-US', {
                weekday: 'long',
                year: 'numeric',
                month: 'long',
                day: 'numeric'
              })}
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
