import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import { TrendingUp, TrendingDown, Activity } from 'lucide-react';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const StockChart = ({ historicalData, stockData, loading, period, onPeriodChange }) => {
  if (loading) {
    return (
      <div className="card h-full">
        <div className="flex items-center justify-center h-full">
          <div className="loading-spinner"></div>
          <span className="ml-2 text-gray-600">Loading chart data...</span>
        </div>
      </div>
    );
  }

  if (!historicalData || !stockData) {
    return (
      <div className="card h-full">
        <div className="flex items-center justify-center h-full text-gray-500">
          <Activity className="w-12 h-12 mr-3" />
          <div>
            <p className="text-lg font-medium">Select a company to view chart</p>
            <p className="text-sm">Choose from the list on the left</p>
          </div>
        </div>
      </div>
    );
  }

  const chartData = {
    labels: historicalData.dates,
    datasets: [
      {
        label: 'Stock Price',
        data: historicalData.prices,
        borderColor: stockData.change >= 0 ? '#22c55e' : '#ef4444',
        backgroundColor: stockData.change >= 0 
          ? 'rgba(34, 197, 94, 0.1)' 
          : 'rgba(239, 68, 68, 0.1)',
        borderWidth: 2,
        fill: true,
        tension: 0.4,
        pointRadius: 0,
        pointHoverRadius: 6,
        pointHoverBackgroundColor: stockData.change >= 0 ? '#22c55e' : '#ef4444',
        pointHoverBorderColor: '#ffffff',
        pointHoverBorderWidth: 2,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        mode: 'index',
        intersect: false,
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        titleColor: '#ffffff',
        bodyColor: '#ffffff',
        borderColor: stockData.change >= 0 ? '#22c55e' : '#ef4444',
        borderWidth: 1,
        callbacks: {
          label: function(context) {
            return `Price: $${context.parsed.y.toFixed(2)}`;
          },
        },
      },
    },
    scales: {
      x: {
        display: true,
        grid: {
          display: false,
        },
        ticks: {
          color: '#6b7280',
          maxTicksLimit: 8,
        },
      },
      y: {
        display: true,
        grid: {
          color: '#f3f4f6',
        },
        ticks: {
          color: '#6b7280',
          callback: function(value) {
            return '$' + value.toFixed(2);
          },
        },
      },
    },
    interaction: {
      mode: 'nearest',
      axis: 'x',
      intersect: false,
    },
  };

  const periodOptions = [
    { value: '1d', label: '1D' },
    { value: '5d', label: '5D' },
    { value: '1mo', label: '1M' },
    { value: '3mo', label: '3M' },
    { value: '6mo', label: '6M' },
    { value: '1y', label: '1Y' },
    { value: '2y', label: '2Y' },
    { value: '5y', label: '5Y' },
  ];

  return (
    <div className="card h-full">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-xl font-bold text-gray-900">
            {stockData.symbol} - {stockData.current_price.toFixed(2)}
          </h2>
          <div className="flex items-center mt-1">
            {stockData.change >= 0 ? (
              <TrendingUp className="w-4 h-4 text-success-600 mr-1" />
            ) : (
              <TrendingDown className="w-4 h-4 text-danger-600 mr-1" />
            )}
            <span
              className={`text-sm font-medium ${
                stockData.change >= 0 ? 'text-success-600' : 'text-danger-600'
              }`}
            >
              {stockData.change >= 0 ? '+' : ''}{stockData.change.toFixed(2)} 
              ({stockData.change_percent >= 0 ? '+' : ''}{stockData.change_percent.toFixed(2)}%)
            </span>
          </div>
        </div>
        
        <div className="flex space-x-1">
          {periodOptions.map((option) => (
            <button
              key={option.value}
              onClick={() => onPeriodChange(option.value)}
              className={`px-3 py-1 text-sm font-medium rounded-lg transition-colors ${
                period === option.value
                  ? 'bg-primary-600 text-white'
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              {option.label}
            </button>
          ))}
        </div>
      </div>

      <div className="h-80">
        <Line data={chartData} options={options} />
      </div>

      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mt-6 pt-6 border-t border-gray-200">
        <div className="text-center">
          <p className="text-sm text-gray-600">Volume</p>
          <p className="text-lg font-semibold text-gray-900">
            {stockData.volume.toLocaleString()}
          </p>
        </div>
        <div className="text-center">
          <p className="text-sm text-gray-600">Market Cap</p>
          <p className="text-lg font-semibold text-gray-900">
            {stockData.market_cap 
              ? `$${(stockData.market_cap / 1e9).toFixed(1)}B`
              : 'N/A'
            }
          </p>
        </div>
        <div className="text-center">
          <p className="text-sm text-gray-600">52W High</p>
          <p className="text-lg font-semibold text-gray-900">
            {stockData.high_52_week ? `$${stockData.high_52_week.toFixed(2)}` : 'N/A'}
          </p>
        </div>
        <div className="text-center">
          <p className="text-sm text-gray-600">52W Low</p>
          <p className="text-lg font-semibold text-gray-900">
            {stockData.low_52_week ? `$${stockData.low_52_week.toFixed(2)}` : 'N/A'}
          </p>
        </div>
      </div>
    </div>
  );
};

export default StockChart;
