import React, { useState, useEffect } from 'react';
import {
  LineChart,
  Line,
  AreaChart,
  Area,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  ComposedChart
} from 'recharts';

const StockChart = ({ historicalData, stockData, loading, period, onPeriodChange }) => {
  const [chartType, setChartType] = useState('line'); // 'line', 'candlestick', 'area', 'volume'
  const [showVolume, setShowVolume] = useState(true);
  const [showMA, setShowMA] = useState(false);

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
    }).format(value);
  };

  const formatVolume = (value) => {
    if (value >= 1e9) return `${(value / 1e9).toFixed(2)}B`;
    if (value >= 1e6) return `${(value / 1e6).toFixed(2)}M`;
    if (value >= 1e3) return `${(value / 1e3).toFixed(2)}K`;
    return value.toLocaleString();
  };

  const calculateMovingAverage = (data, period = 20) => {
    if (!data || data.length < period) return data;
    
    return data.map((item, index) => {
      if (index < period - 1) return item;
      
      const sum = data
        .slice(index - period + 1, index + 1)
        .reduce((acc, val) => acc + val.price, 0);
      const ma = sum / period;
      
      return { ...item, ma };
    });
  };

  const generateCandlestickData = (data) => {
    if (!data || !data.prices || !data.volumes) return [];
    
    return data.prices.map((price, index) => {
      const basePrice = price;
      const variation = 0.02; // 2% variation for mock data
      
      const open = basePrice * (1 + (Math.random() - 0.5) * variation);
      const close = basePrice;
      const high = Math.max(open, close) * (1 + Math.random() * variation * 0.5);
      const low = Math.min(open, close) * (1 - Math.random() * variation * 0.5);
      
      return {
        date: data.dates[index],
        open: parseFloat(open.toFixed(2)),
        high: parseFloat(high.toFixed(2)),
        low: parseFloat(low.toFixed(2)),
        close: parseFloat(close.toFixed(2)),
        volume: data.volumes[index] || 0,
        price: close
      };
    });
  };

  const prepareChartData = () => {
    if (!historicalData || !historicalData.prices) return [];

    const baseData = historicalData.prices.map((price, index) => ({
      date: historicalData.dates[index],
      price: parseFloat(price),
      volume: historicalData.volumes ? historicalData.volumes[index] : 0
    }));

    if (showMA) {
      return calculateMovingAverage(baseData, 20);
    }

    return baseData;
  };

  const renderLineChart = (data) => (
    <ResponsiveContainer width="100%" height={400}>
      <LineChart data={data} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
        <XAxis 
          dataKey="date" 
          stroke="#9CA3AF"
          fontSize={12}
          tickFormatter={(value) => new Date(value).toLocaleDateString()}
        />
        <YAxis 
          stroke="#9CA3AF"
          fontSize={12}
          tickFormatter={formatCurrency}
          domain={['dataMin - 5', 'dataMax + 5']}
        />
        <Tooltip 
          contentStyle={{ 
            backgroundColor: '#1F2937', 
            border: '1px solid #374151',
            borderRadius: '8px',
            color: '#F9FAFB'
          }}
          formatter={(value, name) => [
            name === 'price' ? formatCurrency(value) : formatVolume(value),
            name === 'price' ? 'Price' : 'Volume'
          ]}
          labelFormatter={(label) => new Date(label).toLocaleDateString()}
        />
        <Legend />
        <Line 
          type="monotone" 
          dataKey="price" 
          stroke="#3B82F6" 
          strokeWidth={2}
          dot={false}
          name="Price"
        />
        {showMA && (
          <Line 
            type="monotone" 
            dataKey="ma" 
            stroke="#EF4444" 
            strokeWidth={1}
            dot={false}
            name="20-Day MA"
          />
        )}
      </LineChart>
    </ResponsiveContainer>
  );

  const renderAreaChart = (data) => (
    <ResponsiveContainer width="100%" height={400}>
      <AreaChart data={data} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
        <XAxis 
          dataKey="date" 
          stroke="#9CA3AF"
          fontSize={12}
          tickFormatter={(value) => new Date(value).toLocaleDateString()}
        />
        <YAxis 
          stroke="#9CA3AF"
          fontSize={12}
          tickFormatter={formatCurrency}
          domain={['dataMin - 5', 'dataMax + 5']}
        />
        <Tooltip 
          contentStyle={{ 
            backgroundColor: '#1F2937', 
            border: '1px solid #374151',
            borderRadius: '8px',
            color: '#F9FAFB'
          }}
          formatter={(value, name) => [
            name === 'price' ? formatCurrency(value) : formatVolume(value),
            name === 'price' ? 'Price' : 'Volume'
          ]}
          labelFormatter={(label) => new Date(label).toLocaleDateString()}
        />
        <Legend />
        <Area 
          type="monotone" 
          dataKey="price" 
          stroke="#3B82F6" 
          fill="#3B82F6"
          fillOpacity={0.3}
          strokeWidth={2}
          name="Price"
        />
      </AreaChart>
    </ResponsiveContainer>
  );

  const renderCandlestickChart = (data) => {
    const candlestickData = generateCandlestickData(historicalData);
    
    return (
      <ResponsiveContainer width="100%" height={400}>
        <ComposedChart data={candlestickData} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
          <XAxis 
            dataKey="date" 
            stroke="#9CA3AF"
            fontSize={12}
            tickFormatter={(value) => new Date(value).toLocaleDateString()}
          />
          <YAxis 
            stroke="#9CA3AF"
            fontSize={12}
            tickFormatter={formatCurrency}
            domain={['dataMin - 5', 'dataMax + 5']}
          />
          <Tooltip 
            contentStyle={{ 
              backgroundColor: '#1F2937', 
              border: '1px solid #374151',
              borderRadius: '8px',
              color: '#F9FAFB'
            }}
            formatter={(value, name) => [
              name === 'volume' ? formatVolume(value) : formatCurrency(value),
              name === 'volume' ? 'Volume' : name.charAt(0).toUpperCase() + name.slice(1)
            ]}
            labelFormatter={(label) => new Date(label).toLocaleDateString()}
          />
          <Legend />
          
          {/* Candlestick representation using bars */}
          <Bar dataKey="high" fill="transparent" stroke="#10B981" strokeWidth={1} name="High" />
          <Bar dataKey="low" fill="transparent" stroke="#EF4444" strokeWidth={1} name="Low" />
          <Bar dataKey="close" fill="#3B82F6" stroke="#3B82F6" strokeWidth={1} name="Close" />
          
          {showVolume && (
            <Bar 
              dataKey="volume" 
              fill="#6B7280" 
              fillOpacity={0.3}
              yAxisId={1}
              name="Volume"
            />
          )}
        </ComposedChart>
      </ResponsiveContainer>
    );
  };

  const renderVolumeChart = (data) => (
    <ResponsiveContainer width="100%" height={400}>
      <BarChart data={data} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
        <XAxis 
          dataKey="date" 
          stroke="#9CA3AF"
          fontSize={12}
          tickFormatter={(value) => new Date(value).toLocaleDateString()}
        />
        <YAxis 
          stroke="#9CA3AF"
          fontSize={12}
          tickFormatter={formatVolume}
        />
        <Tooltip 
          contentStyle={{ 
            backgroundColor: '#1F2937', 
            border: '1px solid #374151',
            borderRadius: '8px',
            color: '#F9FAFB'
          }}
          formatter={(value, name) => [formatVolume(value), name]}
          labelFormatter={(label) => new Date(label).toLocaleDateString()}
        />
        <Legend />
        <Bar 
          dataKey="volume" 
          fill="#6B7280" 
          fillOpacity={0.7}
          name="Volume"
        />
      </BarChart>
    </ResponsiveContainer>
  );

  const renderChart = () => {
    const data = prepareChartData();
    
    switch (chartType) {
      case 'line':
        return renderLineChart(data);
      case 'area':
        return renderAreaChart(data);
      case 'candlestick':
        return renderCandlestickChart(data);
      case 'volume':
        return renderVolumeChart(data);
      default:
        return renderLineChart(data);
    }
  };

  if (loading) {
    return (
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/4 mb-4"></div>
          <div className="h-80 bg-gray-200 dark:bg-gray-700 rounded"></div>
        </div>
      </div>
    );
  }

  if (!historicalData || !stockData) {
    return (
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
        <div className="text-center text-gray-500 dark:text-gray-400 py-8">
          Select a company to view stock data
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
      {/* Chart Controls */}
      <div className="flex flex-wrap items-center justify-between mb-6 gap-4">
        <div className="flex items-center space-x-4">
          {/* Chart Type Selector */}
          <div className="flex items-center space-x-2">
            <label className="text-sm font-medium text-gray-700 dark:text-gray-300">
              Chart Type:
            </label>
            <select
              value={chartType}
              onChange={(e) => setChartType(e.target.value)}
              className="px-3 py-1 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-700 text-gray-900 dark:text-white text-sm focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="line">Line Chart</option>
              <option value="area">Area Chart</option>
              <option value="candlestick">Candlestick</option>
              <option value="volume">Volume</option>
            </select>
          </div>

          {/* Period Selector */}
          <div className="flex items-center space-x-2">
            <label className="text-sm font-medium text-gray-700 dark:text-gray-300">
              Period:
            </label>
            <select
              value={period}
              onChange={(e) => onPeriodChange(e.target.value)}
              className="px-3 py-1 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-700 text-gray-900 dark:text-white text-sm focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="1d">1 Day</option>
              <option value="5d">5 Days</option>
              <option value="1mo">1 Month</option>
              <option value="3mo">3 Months</option>
              <option value="6mo">6 Months</option>
              <option value="1y">1 Year</option>
              <option value="2y">2 Years</option>
              <option value="5y">5 Years</option>
            </select>
          </div>

          {/* Chart Options */}
          {chartType === 'line' && (
            <div className="flex items-center space-x-4">
              <label className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={showMA}
                  onChange={(e) => setShowMA(e.target.checked)}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-sm text-gray-700 dark:text-gray-300">20-Day MA</span>
              </label>
            </div>
          )}

          {chartType === 'candlestick' && (
            <div className="flex items-center space-x-4">
              <label className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={showVolume}
                  onChange={(e) => setShowVolume(e.target.checked)}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-sm text-gray-700 dark:text-gray-300">Show Volume</span>
              </label>
            </div>
          )}
        </div>

        {/* Stock Info */}
        <div className="text-right">
          <div className="text-2xl font-bold text-gray-900 dark:text-white">
            {formatCurrency(stockData.current_price)}
          </div>
          <div className={`text-sm font-medium ${
            stockData.change >= 0 
              ? 'text-green-600 dark:text-green-400' 
              : 'text-red-600 dark:text-red-400'
          }`}>
            {stockData.change >= 0 ? '+' : ''}{stockData.change} ({stockData.change_percent >= 0 ? '+' : ''}{stockData.change_percent}%)
          </div>
        </div>
      </div>

      {/* Chart */}
      <div className="w-full">
        {renderChart()}
      </div>

      {/* Additional Info */}
      <div className="mt-6 grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
        <div>
          <span className="text-gray-500 dark:text-gray-400">Volume:</span>
          <span className="ml-2 text-gray-900 dark:text-white">
            {formatVolume(stockData.volume)}
          </span>
        </div>
        <div>
          <span className="text-gray-500 dark:text-gray-400">Market Cap:</span>
          <span className="ml-2 text-gray-900 dark:text-white">
            {stockData.market_cap ? `$${(stockData.market_cap / 1e9).toFixed(2)}B` : 'N/A'}
          </span>
        </div>
        <div>
          <span className="text-gray-500 dark:text-gray-400">P/E Ratio:</span>
          <span className="ml-2 text-gray-900 dark:text-white">
            {stockData.pe_ratio ? stockData.pe_ratio.toFixed(2) : 'N/A'}
          </span>
        </div>
        <div>
          <span className="text-gray-500 dark:text-gray-400">Dividend Yield:</span>
          <span className="ml-2 text-gray-900 dark:text-white">
            {stockData.dividend_yield ? `${(stockData.dividend_yield * 100).toFixed(2)}%` : 'N/A'}
          </span>
        </div>
      </div>
    </div>
  );
};

export default StockChart;
